
public class whilepractice {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i,sum;
		i = 1;
		sum = 0;
		System.out.println("1부터 10까 지 더하는 프로그램");
		while(i<=10){
			sum = sum + i;
			i++;
			
		}
		System.out.println("합계는" + sum + "이다");}
	}


